css:
body {
  line-height: 1.75;
}
p {
  text-indent: 2rem;
  margin: 0 0 0.8em;
}
.chapter-header {
  margin: 1.25em 0 1em;
}
.chapter-stamp {
  display: inline-block;
  margin: 0 0 0.45em;
  padding: 0.1em 0.5em;
  font-size: 0.72em;
  letter-spacing: 0.1em;
  border: 1px solid currentColor;
  border-radius: 999px;
  text-indent: 0;
}
.chapter-title {
  margin: 0;
  font-size: 1.42em;
  font-weight: 760;
  line-height: 1.28;
}
.front-matter p.author {
  text-align: center;
  text-indent: 0;
  margin: 0 0 1.5em;
}
.front-matter p.intro-label {
  text-indent: 0;
  font-weight: 700;
  margin: 1.2em 0 0.6em;
}
.volume p, .volume .chapter-title {
  text-indent: 0;
}
